import React, { useState } from 'react'

const BabiesList = ({ names }) => {
  const [isFavorite, setIsFavorite] = useState(false)
  const [babyNames, setFilter] = useState(
    names.sort((a, b) => a.name.localeCompare(b.name)),
    )

  const filter = (inputValue) => {
    setFilter(
      names.filter((baby) =>
        baby.name.toLowerCase().includes(inputValue.toLowerCase()),
      ),
    )
  }

  const moveToFavorites = (element) => {
    document.getElementById('favorites').appendChild(element)
  }
  return (
    <>
      <div className="content">
        <input
          className="form-control"
          type="text"
          placeholder="Search for a name"
          onChange={(e) => filter(e.target.value)}
        />
      </div>
      <div className="favorites" id="favorites">
        Favorites : 
      </div>
      <div className="content">
        {babyNames.map((baby, index) => (
          <span
            key={index}
            id={index}
            className={baby.sex === 'f' ? 'girl' : 'boy'}
            onClick={(event) => {
              moveToFavorites(event.target)

              setIsFavorite(!isFavorite)
            }

            } 
          >
            {baby.name}
          </span>
        ))}
      </div>
    </>
  )
}

export default BabiesList
