import React, { useState } from 'react'

const favDiv = document.getElementById('favorites')
const listDiv = document.getElementById('list')

const favoritesList = localStorage.getItem('favoritesList')

const loadFavorites = () => {
  favoritesList.map((fav) => favDiv.appendChild(fav))
}
const MakeName = ({ baby, index }) => {
  const [isFavorite, setIsFavorite] = useState(false)

  const moveToFavorites = (element) => {
    favDiv.appendChild(element)
    favoritesList.push(element)
  }

  const removeFromFavorites = (element) => {
    listDiv.appendChild(element)
    favoritesList.splice(favoritesList.indexOf(element), 1)
  }
  favoritesList.length>0&&loadFavorites()
  return (
    <span
      key={index}
      id={index}
      className={baby.sex === 'f' ? 'girl' : 'boy'}
      onClick={(event) => {
        isFavorite
          ? removeFromFavorites(event.target)
          : moveToFavorites(event.target)

        setIsFavorite(!isFavorite)
      }}
    >
      {baby.name}
    </span>
  )
}

window.close(localStorage.setItem('favoritesList', favoritesList))

export default MakeName
