import React, { useState, useEffect } from 'react'
import MakeName from './MakeName'

const BabiesList = ({ names }) => {
  const [genderFilter, setGenderFilter] = useState('all')
  const [search, setSearch] = useState('')
  const [list, setList] = useState(
    names.sort((a, b) => a.name.localeCompare(b.name)),
  )

  useEffect(() => {
    if (search !== '')
      setList(
        list.filter((baby) =>
          baby.name.toLowerCase().includes(search.toLowerCase()),
        ),
      )
    if (genderFilter !== 'all')
      setList(list.filter((baby) => baby.sex === genderFilter))
  }, [search, genderFilter])

  return (
    <>
      <div className="input-group">
        <input
          className="form-control m-2"
          type="text"
          placeholder="Search for a name"
          onChange={(e) => setSearch(e.target.value)}
        />
        <a onClick={() => setGenderFilter('all')}>
          <img src="./img/baby-name-all.png" alt="baby name all" />
        </a>
        <a href={() => setGenderFilter('f')}>
        <img src="./img/baby-name-girl.png" alt="baby name girl" />
        </a>
        <a href={() => setGenderFilter('m')}>
        <img src="./img/baby-name-boy.png" alt="baby name boy" />
        </a>
      </div>
      <div className="favorites" id="favorites">
        Favorites :
      </div>
      <hr />
      <div className="content" id="list">
        {list.map((baby, index) => (
          <MakeName baby={baby} index={index} />
        ))}
      </div>
    </>
  )
}

export default BabiesList
